public class Lucas {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		new Lucas().init();
	}

	public void init(){
		Scanner scan = new Scanner(System.in);
		while(scan.hasNext()){
			String n = scan.nextLine();
			System.out.println(n);
			String line = scan.nextLine();
			System.out.println(line);
			
		}
	}
}
